import FormInput from "../../components/FormInput"
import Button from "../../components/Button"

const SignIn = () => {
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  return (
    <div className="App">
    <header className="App-header">
      <div className="body">
      <img alt="codemasters" src={window.location.origin+"/codemaster.png"} className="codemaster"/>
      <p className="signin">
        SIGN IN
      </p>
      <p className="credential">
        Enter your credentials to access your account
      </p>
      <FormInput
            name="email"
            value={email}
            onChange={e => {
              setEmail(e.target.value);
            }}
            label="Email"
            placeholder="Enter your Email"
          />
          <FormInput
            name="password"
            value={password}
            onChange={e => {
              console.log(e)
              setPassword(e.target.value);
            }}
            label="Password"
            placeholder="Enter your Password"
            type="password"
          />
   <Button />
      <p className="reset">Forgot your password ?<a href="www.codemasters.id" className="link">Reset Password</a></p>
      </div>
    </header>
  </div>
  )
}

export default SignIn
