import { Flex } from '@chakra-ui/react';
import dayjs from 'dayjs';
import { useEffect, useState } from 'react';
import FormInput from '../../../components/FormInput';
import Modal from '../../../components/Modal';
import { useStudent } from '../../../services';
import { useCustomToast } from '../../../utils';

const initialState = {
  name: '',
  email: '',
  phoneNumber: '',
  enrollNumber: '',
  admissionDate: '',
};

function ModalFormStudent({ data, onClose, isOpen, refresh }) {
  const { showToastError, showToastSuccess } = useCustomToast();
  const { addStudent, updateStudent } = useStudent();
  const [form, setForm] = useState(initialState);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (data) {
      setForm({ ...data });
    } else {
      setForm(initialState);
    }
  }, [isOpen]);

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleAdd = async () => {
    setIsLoading(true);
    try {
      const res = await addStudent({
        ...form,
        admissionDate: dayjs(form.admissionDate).format('YYYY-MM-DD'),
      });
      showToastSuccess(res?.message);
      onClose();
      refresh();
    } catch (e) {
      showToastError(e);
    }
    setIsLoading(false);
  };

  const handleUpdate = async () => {
    setIsLoading(true);
    try {
        const res =await updateStudent(data?.id, {...form, admissionDate: dayjs(form.admissionDate).format('YYY-MM-DD')})
        showToastSuccess(res?.message)
        onClose()
        refresh();
    } catch (e) {
        showToastError(e);
    }
    setIsLoading(false);
  }

  return (
    <Modal >
        <Flex>
            <FormInput name='name' value={form.name} onChange={handleChange} label='Name' placeholder='Karthi'/>
            <FormInput name='email' value={form.email} onChange={handleChange} label='Email' placeholder='karthi@gmail.com'/>
            <FormInput name='phoneNumber' value={form.enrollNumber} onChange={handleChange} label='Phone' placeholder='085XXX'/>
            <FormInput name='enrollNumber' value={form.enrollNumber} onChange={handleChange} label='Enroll Number' placeholder='123456'/>
            <FormInput type='date' value={form.admissionDate} onChange={handleChange} label='Date of Admission' placeholder='2023-12-31'/>
        </Flex>
    </Modal>
  );
}

export default ModalFormStudent;
