import {
  Box,
  Flex,
  IconButton,
  Image,
  Table,
  TableContainer,
  Tbody,
  Td,
  Text,
  Th,
  Thead,
  Tr,
} from '@chakra-ui/react';
import Button from '../../components/Button';
import Modal from '../../components/Modal';
import Pagination from '../../components/Pagination';
import Constants from '../../constants';
import { BiEdit, BiTrash, BiTrashAlt } from 'react-icons/bi';
import { useSearchParams } from 'react-router-dom';

const Course = () => {
  return (
    <Box>
      <TableContainer>
        <Flex justifyContent="space-between">
          <Text color="gray.900" fontSize="24px" mb="24px">
            Student List
          </Text>
          <Button
            text="ADD NEW STUDENT"
            isBgGradient
            onClick={() => setShowFormPopup(true)}
          />
        </Flex>
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th color="gray.500"></Th>
              <Th color="gray.500">Name</Th>
              <Th color="gray.500">Email</Th>
              <Th color="gray.500">Phone</Th>
              <Th color="gray.500">Enroll Number</Th>
              <Th color="gray.500">Date of Admission</Th>
              <Th color="gray.500"></Th>
            </Tr>
          </Thead>
          <Tbody>
            {data.map(item => {
              return (
                <Tr bgColor="gray.50" key={`payment-item-${item.id}`}>
                  <Td>
                    <Box
                      width="65px"
                      height="55px"
                      overflow="hidden"
                      borderRadius="8px"
                    >
                      <Image
                        src={Constants.STUDENT_IMAGE}
                        width="100%"
                        height="100%"
                        objectFit="initial"
                      />
                    </Box>
                  </Td>
                  <Td fontSize="14px" color="gray.900">
                    {item.name}
                  </Td>
                  <Td fontSize="14px" color="gray.900">
                    {item.email}
                  </Td>
                  <Td fontSize="14px" color="gray.900">
                    {item.phoneNumber}
                  </Td>
                  <Td fontSize="14px" color="gray.900">
                    {item.enrollNumber}
                  </Td>
                  <Td fontSize="14px" color="gray.900">
                    {dayjs(item.admissionDate).format('YYYY-MM-DD')}
                  </Td>
                  <Td>
                    <Flex gap="10px">
                      <IconButton
                        onClick={() => handleEdit(item)}
                        size="xs"
                        variant="ghost"
                        aria-label="open menu"
                        color="#667085"
                        icon={<BiEdit />}
                      />
                      <IconButton
                        onClick={() => handleOpenDeletePopup(item)}
                        size="xs"
                        variant="ghost"
                        aria-label="open menu"
                        color="#667085"
                        icon={<BiTrashAlt />}
                      />
                    </Flex>
                  </Td>
                </Tr>
              );
            })}
          </Tbody>
        </Table>
      </TableContainer>
      <Pagination links={links} onClick={getListData} />
      <Modal
        isOpen={showDeletePopup}
        title="Delete Student"
        confirmButtonText="Delete"
        onClose={handleCloseDeletePopup}
        onConfirm={handleDelete}
      >
        <Text fontSize="14px" color="gray.400">
          Yakin ?
        </Text>
      </Modal>
      <ModalFormStudent
        refresh={getListData}
        data={selectedData}
        isOpen={showFormPopup}
        onClose={handleCloseFormPopup}
      />
    </Box>
  );
};

export default Course;
