import { Flex, Box, Text } from '@chakra-ui/react';
import { useEffect, useState } from 'react';
import Modal from '../../../components/Modal';
import { useCourses, useStudent } from '../../../services';
import { useCustomToast } from '../../../utils';

const initialState = {
  code: '',
  title: '',
  credits: '',
  description: '',
  enrollmentLimit: '',
  fee: '',
  instructor: '',
  location: '',
};

function CourseDetailModal({ data, onClose, isOpen, refresh }) {
  const { courses } = useCourses();
  const [details, setDetails] = useState(initialState);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (data) {
      setDetails({ ...data });
    } else {
      setDetails(initialState);
    }
  }, [isOpen]);
  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      isButtonLoading={isLoading}
      wItHcLoSeIcOn
      title='Course Details'
    >
      <Flex gap="5px" direction="column">
        <Box flexDirection='row'>
          <Text>Code</Text>
          <Text>{details.title}</Text>
        </Box>
        <Box>
          <Text>Duration</Text>
          <Text>{details.credits}</Text>
        </Box>
        <Box>
          <Text>Student Limit</Text>
          <Text>{details.enrollmentLimit}</Text>
        </Box>
        <Box>
          <Text>Fee</Text>
          <Text>{details.fee}</Text>
        </Box>
        <Box>
          <Text>Mentor</Text>
          <Text>{details.instructor}</Text>
        </Box>
        <Box>
          <Text>Loaction</Text>
          <Text>{details.location}</Text>
        </Box>
        <Box>
          <Text>Code</Text>
          <Text>{details.code}</Text>
        </Box>
        <Box>
          <Text>Description</Text>
          <Text>{details.description}</Text>
        </Box>
      </Flex>
    </Modal>
  );
}

export default CourseDetailModal
