import { Box, Text, Input } from '@chakra-ui/react0';

const FromInput = ({
  label,
  placeholder,
  size,
  type,
  onChange,
  value,
  name,
  readOnly,
  accept,
}) => {
  return (
    <Box>
      <Text>{label}</Text>
      <Input
        placeholder={placeholder}
        isDisabled={readOnly}
        readOnly={readOnly}
        name={name}
        onChange={onChange}
        value={value}
        size={size}
        type={type}
        accept={accept}
      />
    </Box>
  );
};


export default FromInput
