import { Box, Image } from '@chakra-ui/react';
import useCustomToast from '../utils/useCustomToast';
import { useEffect } from 'react';
import Button from './Button';
import FormInput from './FormInput';


const UnderContruction = () => {
  const { showToastError, showToastSuccess } = useCustomToast();
  const onRender = () => {
    showToastError('404 : Not Found');
  };

  useEffect(() => {
    setTimeout(() => {
      onRender()
    }, 3000)
  }, [])

  return (
    <Box display="flex" justifyContent="center">
      <Image
        src={window.location.origin + '/image.jpg'}
        alt="Not Found"
        boxSize="100%"
        maxWidth="100vw"
        maxHeight="100vh"
        fit="contain"
      />
      <Button 
      text='Ini adalah Tombol'
      isBgGradient={true}
      />
      <FormInput 
      label='Ini Adalah Form Input'
      />
    </Box>
  );
};

export default UnderContruction;
